import LoginIcon from "@heroicons/react/outline/LoginIcon";
import UserIcon from "@heroicons/react/outline/UserIcon";
import SignOut from "@heroicons/react/outline/LogoutIcon";
import { Link, useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { logout, reset } from "../features/auth/authSlice";
function Header() {
  const NavItems = [
    {
      title: "Login",
      link: "login",
      icon: <LoginIcon />,
    },
    {
      title: "Logout",
      link: "logout",
      icon: <SignOut />,
    },
    {
      title: "Register",
      link: "register",
      icon: <UserIcon />,
    },
  ];
  return (
    <header className="flex justify-between items-center p-2 h-20">
      <div>
        <Link to="/" className="font-bold text-xl whitespace-nowrap">
          Taskerr. üìù
        </Link>
      </div>
      <ul className="flex">
        {NavItems.map((item, idx) => (
          <Link
            to={item.link}
            key={idx}
            className="flex items-center mr-2 lg:mr-5 font-bold"
          >
            <div className="text-third h-6 w-6 mr-1">{item.icon}</div>{" "}
            {item.title}
          </Link>
        ))}
      </ul>
    </header>
  );
}

export default Header;
