import { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { authenticate, reset } from "../features/auth/authSlice";
import Spinner from "../components/Spinner";
import UserIcon from "@heroicons/react/outline/UserIcon";

function Login() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  const { username, password } = formData;

  const { user, isLoading, isError, isSuccess, message } = useSelector(
    (state) => state.auth
  );
  useEffect(() => {
    if (isError) {
      return;
    }
    if (isSuccess || user) {
      navigate("/");
    }

    dispatch(reset());
  }, [user, isError, isSuccess, isLoading, message, navigate, dispatch]);

  const onChange = (e) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };
  const onSubmit = (e) => {
    e.preventDefault();
    const userData = {
      username,
      password,
    };
    dispatch(authenticate(userData));
  };

  if (isLoading) {
    return <Spinner />;
  }

  console.log(isError);
  return (
    <div className="  flex flex-col justify-between h-[90%] ">
      <section className="flex flex-col items-center text-black mb-2 h-full justify-between">
        <div className="  bg-third flex flex-col w-full items-center rounded-b-lg ">
          <UserIcon className="h-10" />
          <h1 className=" font-bold">Login</h1>
        </div>
        <h1 className=" font-bold">Login and take care of the tasks!</h1>
      </section>
      <section className="flex flex-col justify-between p-4 ">
        <form
          action="POST"
          onSubmit={onSubmit}
          className="flex flex-col h-full"
        >
          <div className="">
            {isError ? (
              <div className="bg-red-500 p-4 rounded-lg text-white animate-bounce temporary-bounce">
                Bad credentials
              </div>
            ) : (
              <></>
            )}
            <div className="flex">
              <input
                type="text"
                id="username"
                name="username"
                value={username}
                placeholder="Username..."
                onChange={onChange}
                className="flex-grow  rounded-lg p-4 mb-2 bg-secondary"
              />
            </div>
            <div className="flex">
              <input
                type="password"
                id="password"
                name="password"
                value={password}
                placeholder="Password..."
                onChange={onChange}
                className=" rounded-lg p-4 mb-2 bg-secondary flex-grow"
              />
            </div>
          </div>
          <div className="">
            <button
              type="submit"
              className="bg-third hover:bg-opacity-90 text-black w-full p-3 font-bold text-xl rounded-lg mt-2"
            >
              Login
            </button>
            <div className="text-center mt-2 text-gray-600">
              Already have an account?
            </div>
          </div>
        </form>
      </section>
    </div>
  );
}

export default Login;
