import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import UserIcon from "@heroicons/react/outline/UserIcon";

function Register() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
  });
  const { email, password, firstName, lastName } = formData;

  //   useEffect(() => {});
  const onChange = (e) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };
  const onSubmit = (e) => {
    e.preventDefault();
  };
  return (
    <div className=" flex flex-col justify-between h-[90%]">
      <section className="bg-third flex flex-col items-center text-black mb-2 rounded-b-lg py-2">
        <div className="h-10 w-10">
          <UserIcon />
        </div>
        <h1 className=" font-bold">Register</h1>
      </section>
      <section className="flex flex-col p-4">
        <form
          action="POST"
          onSubmit={onSubmit}
          className="flex flex-col h-full"
        >
          <div>
            <div className="flex">
              <input
                type="text"
                id="firstName"
                name="firstName"
                value={firstName}
                placeholder="First name..."
                onChange={onChange}
                className="flex-grow  rounded-lg p-4 mb-2 bg-secondary"
              />
            </div>
            <div className="flex">
              <input
                type="text"
                id="lastName"
                name="lastName"
                value={lastName}
                placeholder="Last name..."
                onChange={onChange}
                className="flex-grow  rounded-lg p-4 mb-2 bg-secondary"
              />
            </div>
            <div className="flex">
              <input
                type="email"
                id="email"
                name="email"
                value={email}
                placeholder="Email..."
                onChange={onChange}
                className="flex-grow  rounded-lg p-4 mb-2 bg-secondary"
              />
            </div>
            <div className="flex">
              <input
                type="password"
                id="password"
                name="password"
                value={password}
                placeholder="Password..."
                onChange={onChange}
                className=" rounded-lg p-4 mb-2 bg-secondary flex-grow"
              />
            </div>
          </div>
          <div>
            <button
              type="submit"
              className="bg-third text-black w-full p-5 font-bold text-xl rounded-lg mt-2"
            >
              Create Account
            </button>
            <div className="text-center mt-2 text-gray-600">
              Already have an account?
            </div>
          </div>
        </form>
      </section>
    </div>
  );
}

export default Register;
